import { store } from 'shared:/states/store';
import Navbar from "./Navbar";
import LogInPage from "./LoginPage"
import Logout from "./Logout";
import EditProfile from "./EditProfile";
import Register from "./Register";

import { useState, useEffect } from "react";
import { Route, Switch, Link, } from "react-router-dom";
import { Header, Segment, } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";


//npm i && npm start
//npm install react-router-dom
//npm install semantic-ui-react semantic-ui-css
//npm install axios

function App() {

  const [user, setUser] = useState(localStorage.getItem("user"));

  useEffect(() => {
    fetch("http://localhost:9292/me", { credentials: "include" })
      .then((r) => r.json())
      .then((data) => {
        setUser(data.user);
        localStorage.setItem("user", data.user);
      });
  }, []);

  //check to see if we have a cookie that
  //check the state constantly
  //check the state when state will lose the user

  return (
    <div>
      <Navbar
        user={user}
        // cartDrinks={cartDrinks}
        style={{ marginBottom: "0px", paddingBottom: "0px" }}
      />
      <Segment style={{ marginTop: "0px" }}>
        <Link to="/">
          <div className="ui center aligned header"> 
          {/* CHANGED THIS */}
            <Header as="h2">
              Welcome to Starbooks
            </Header>
          </div>
        </Link>
      </Segment>
      <Switch>
        <Route exact path="/login">
          <LogInPage setUser={setUser} user={user} />
        </Route>
        <Route exact path="/logout">
          <Logout setUser={setUser} />
        </Route>

        <Route exact path="/register">
          <Register setUser={setUser}/>
        </Route>

        <Route exact path="/edit-profile">
          <EditProfile user={user} setUser={setUser} />
        </Route>

        <Route path="*">
          <h1>404 not found</h1>
        </Route>
      </Switch>
      {/* {user ? null : <Redirect to="/login" />} */}
    </div>
  );
}

export default App;
